mapper.select:
WITH first_image AS ( SELECT REST_NO, REST_IMG_NAME, REST_IMG_NO, ROW_NUMBER() OVER (PARTITION BY REST_NO ORDER BY REST_IMG_NO) AS rn FROM REST_IMG ) SELECT * FROM ( SELECT r.REST_NO, r.REST_NAME, r.REST_PHONE, r.REST_ADR, r.REST_BH, r.REST_LOC, r.REST_CATE, i.REST_IMG_NAME, i.REST_IMG_NO FROM RESTAURANT r LEFT JOIN first_image i ON r.REST_NO = i.REST_NO AND i.rn = 1
WHERE r.REST_CATE IN (
#{category}
)
ORDER BY DBMS_RANDOM.VALUE ) WHERE ROWNUM <= 300

mapper.update:
UPDATE RESERVE 
SET
	RES_MEMO=#{res_memo}
WHERE
	RES_NO=#{res_no}
