mapper.select:
SELECT r.REST_NO, r.REST_NAME, r.REST_PHONE, r.REST_ADR, r.REST_BH, r.REST_LOC, r.REST_CATE, i.REST_IMG_NAME, i.REST_IMG_NO FROM RESTAURANT r LEFT JOIN ( SELECT REST_NO, REST_IMG_NAME, REST_IMG_NO, ROW_NUMBER() OVER (PARTITION BY REST_NO ORDER BY REST_IMG_NO) AS rn FROM REST_IMG ) i ON r.REST_NO = i.REST_NO AND i.rn = 1 WHERE 1=1
AND r.rest_loc LIKE #{region}
AND r.rest_cate = #{category}

SELECT r.REST_NO, r.REST_NAME, r.REST_PHONE, r.REST_ADR, r.REST_BH, r.REST_LOC, r.REST_CATE, i.REST_IMG_NAME, i.REST_IMG_NO FROM RESTAURANT r LEFT JOIN ( SELECT REST_NO, REST_IMG_NAME, REST_IMG_NO, ROW_NUMBER() OVER (PARTITION BY REST_NO ORDER BY REST_IMG_NO) AS rn FROM REST_IMG ) i ON r.REST_NO = i.REST_NO AND i.rn = 1
AND ( UPPER(r.rest_name) LIKE '%' || UPPER(#{keyword}) || '%' )
AND r.rest_loc LIKE #{region}
AND r.rest_cate = #{category}
ORDER BY CASE WHEN UPPER(r.rest_name) = UPPER(#{keyword}) THEN 0 ELSE 1 END, r.rest_name ASC
